import React from "react";
import { Link } from 'react-router-dom';
import { ApiDelete } from '../common/Api';

const PersonTable = (props) => {
  const role = props.role;

  const deletee = (id, role) => {
    ApiDelete("/api/article/" + id)
        .then((data) => props.delete())
        .catch(async (error) => {
            error = await error;
            if (error.success === false) {
                alert(error.message);
            }
        });
  };

  return (
    <div>
      <p>
        {props.label} {props.items.length}
      </p>

      <table className="table table-bordered">
       

        <tbody>
          {props.items.map((item, index) => (
            <tr key={index + 1}>

              
              <td>
                <div className="btn-group">
                  <Link to={"/article/show/" + item._id} className="btn btn-sm btn-info">{item.name}</Link>
                  <Link to={'/article/edit/' + item._id} className="btn btn-sm btn-warning"> Upravit </Link>
                  <button onClick={deletee.bind(this, item._id)} className="btn btn-sm btn-danger"> Odstranit </button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {props.link ? (
                <Link to={'/article/create'} className="btn btn-success">
                    Nový článek
                </Link>
            ) : null}
    </div>
  );
};

export default PersonTable;
