import React from 'react';
import ArticleIndex from '../articles/ArticleIndex';
import {
    useLocation,
  } from "react-router-dom";
  
  
  const Writer = (props) => {
    const location = useLocation();
    const url = location.pathname;
    return (
        <div>
            
            {url === '/writer' && <ArticleIndex 
            logged = {props.logged}/>}
            {url === '/writer/article/show/:id' && <ArticleDetail 
            logged = {props.logged}/>}
            {(url === '/article/create' || url === '/article/edit/:id') && <ArticleForm 
            logged = {props.logged}/>}

           
        </div>
    );
}

export default Writer;
