import React, { useState, useEffect } from 'react';
import { ApiGet, ApiPost, ApiPut } from '../common/Api';
import DateStringFormatter from '../common/DateStringFormatter';
import FlashMessage from '../common/FlashMessage';
import InputField from '../common/InputField';
import InputCheck from '../common/InputCheck';
import Role from './Role';
import { useLocation, useParams } from "react-router";


const PersonForm = (props) => {
    const location = useLocation();
    const [personIdState, setPersonId] = useState(null);
    const [personNameState, setPersonName] = useState('');
    const [biographyState, setBiography] = useState('');
    const [personRoleState, setPersonRole] = useState('');
    const [sentState, setSent] = useState(false);
    const [successState, setSuccess] = useState(false);
    const [errorState, setError] = useState(null);

    //params in props.match.params.id byly undefined, tenhle statement to vyřešil 
    const { id } = useParams();
        
        useEffect(() => {
            if (id) {
            setPersonId(id);
        
            ApiGet("/api/article/" + id).then((data) => {
                setPersonName(data.name);
                setBiography(data.biography);
                setPersonRole(data.role);
            });
            }
        }, [id ]);
    
    

      const handleSubmit = (e) => {
        e.preventDefault();

        const body = {
            name: personNameState,
            biography: biographyState,
            role: personRoleState,
        };

        (personIdState
            ? ApiPut('/api/article/' + id, body)
            : ApiPost('/api/article/', body)
        )
            .then((data) => {
                setSent(true);
                setSuccess(true);
               
            })
            .catch( async (error) => {
                const er = await error
                console.log('here', er.message)
                setError(er.message)
                setSent(true);
                setSuccess(false);
            });

    }
    
    const sent = sentState;
    const success = successState;
    
    
        return (
            <div>
                <h1>{ id ? 'Upravit' : 'Vytvořit'} článek</h1>
                <hr />
                {errorState? <div className='alert alert-danger'>{errorState}</div> : ''}
                {sent && (
                    <FlashMessage
                        theme={success ? 'success' : ''}
                        text={
                            success
                                ? 'Uložení osobnosti proběhlo úspěšně.'
                                : ''
                        }
                    />
                )}
    
                <form onSubmit={handleSubmit}>
                    <InputField
                        required={true}
                        type="text"
                        name="personName"
                        min="3"
                        label="Název článku"
                        prompt="Např. Teorie světa"
                        value={personNameState}
                        handleChange={(e) => {
                        const { value } = e.target;
                        setPersonName(value);
                        console.log(personNameState);
                        }}
                    />

                    <InputField
                        required={true}
                        type="textarea"
                        name="biography"
                        minLength="10"
                        label="Text článku"
                        prompt="Napište text článku"
                        rows="5"
                        value={biographyState}
                        handleChange={(e) => {
                        const { value } = e.target;
                        setBiography(value);
                        console.log(biographyState);
                        }}
                    />

                    <input type="submit" className="btn btn-primary" value="Uložit" />
                </form>
            </div>
        );
}

export default PersonForm;
