import React, { useState, useEffect } from "react";
import {
  BrowserRouter ,
  Route,
  useNavigate,
  Navigate,
  Routes,
  useLocation,
  Link
} from "react-router-dom";
import Programmer from './Programmer/Programmer';
import WebCreator from './WebCreator/WebCreator';
import Writer from './Writer/Writer';
import MusicMaker from './MusicMaker/MusicMaker';
import Header from './Header/Header';
//css
import styles from './App.module.scss';

import Home from './Home/Home';
import EditorApp from './EditorApp/EditorApp';
import LoginPage from './auth/Login';
import RegisterPage from './auth/Register';
import MovieDetail from './movies/MovieDetail';
import MovieForm from './movies/MovieForm';
import MovieIndex from './movies/MovieIndex';
import PersonIndex from './persons/PersonIndex';
import PersonDetail from './persons/PersonDetail';
import PersonForm from './persons/PersonForm';




require('./cssReset.scss');

const App = () => {
  
  const location = useLocation();
  const url = location.pathname === "/home";

  const navigate = useNavigate();
  const [loggedIn, setloggedIn] = useState(false);

  const loadId = () => {
    const islogin = localStorage.getItem("is-login");
    console.log(typeof islogin + "___");
   
      setloggedIn(islogin);
      if (islogin === true) {
        navigate("/articles");
          <Navigate to="/article" />;
      } else {
        navigate("/login");
     
      }
      
    
  };

  const logOut = () => {
    localStorage.setItem("is-login", "false");
    const islogin = localStorage.removeItem("is-login");
    if ( typeof islogin === 'boolean') {
      setloggedIn(islogin);
    }
  };

  useEffect(() => {
      loadId();
      if (loggedIn) {
        navigate("/article");
          
      } else {
        navigate("/login");
          
      }
  }, [loggedIn]);
  
  return (
    
    <div className={styles.appContainer}>
      <div className={styles.app}>
      
      { !url && <Header url={url}/>}
      
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
          {!loggedIn || loggedIn === null ? (
              <ul className="navbar-nav mr-auto">
                  <li className="nav-item">
                      <Link to={"/login"} className="nav-link">
                          Přihlásit se
                      </Link>
                  </li>
                  <li className="nav-item">
                      <Link to={"/register"} className="nav-link">
                          Registrovat se
                      </Link>
                  </li>
              </ul>
          ) : (
              <ul className="navbar-nav mr-auto">
                  <li className="nav-item">
                      <Link to={"/movies"} className="nav-link">
                          Filmy
                      </Link>
                  </li>
                  <li className="nav-item">
                      <Link to={"/article"} className="nav-link">
                          Osobnosti
                      </Link>
                  </li>
                  <li className="nav-item" onClick={logOut}>
                      <Link to={"#"} className="nav-link">
                          Odhlásit se
                      </Link>
                  </li>
              </ul>
          )}
      </nav>

      <Routes>

        <Route path="/login" element={<LoginPage/>} />
        <Route path="/register" element={<RegisterPage/>} />
        <Route exact path="/movies" element={<MovieIndex/>} />
        <Route path="/movies/show/:id" element={<MovieDetail/>} />
        <Route path="/movies/create" element={<MovieForm/>} />
        <Route path="/movies/edit/:id" element={<MovieForm/>} />

        <Route exact path="/article" element={<PersonIndex/>} />
        <Route path="/article/show/:id" element={<PersonDetail/>} />
        <Route path="/article/create" element={<PersonForm/>} />
        <Route path="/article/edit/:id" element={<PersonForm/>} />

        <Route path="/" element={<Navigate replace to="/home" />} />
        <Route path="/home" element={<Home/>} />
        <Route path="/programmer" element={<Programmer/>} />
        <Route path="/programmer/editor" element={<EditorApp/>} />
        <Route path="/webmaker" element={<WebCreator/>} />
        <Route path="/writer" element={<Writer/>} />
        <Route path="/musicmaker" element={<MusicMaker/>} />
      </Routes>
        

      </div>
    </div>
  );
}

export default App;
